const allButtons=document.querySelectorAll(".spinButton");let buttons=[];class button{constructor(targetBtn,text,icon,btnWrap){this.targetBtn=targetBtn;this.text=text;this.icon=icon;this.btnWrap=btnWrap}}allButtons.forEach(btn=>{const btnWrap=btn.querySelector(".button-wrap-button");const btnText=btn.querySelector(".button-wrap-button-text");const btnIcon=btn.querySelector(".button-wrap-button-image");newButton=new button(btnWrap,btnText,btnIcon,btn);buttons.push(newButton)});buttons.forEach(btn=>{btn.btnWrap.addEventListener("mouseenter",e=>{setTimeout(()=>{btnHover(btn)},200)});btn.btnWrap.addEventListener("touchstart",e=>{setTimeout(()=>{btnHover(btn)},200)},{passive:true});btn.btnWrap.addEventListener("mouseleave",e=>{setTimeout(()=>{btnLeave(btn)},200)})});function btnHover(target){target.icon.style.removeProperty("transform");let tl=anime.timeline({easing:"easeOutElastic",duration:1e3});target.text.style.display="none";target.icon.style.display="block";tl.add({targets:target.targetBtn,width:"5rem"},0).add({targets:target.icon,rotate:360},0);tl.restart()}function btnLeave(target){target.icon.style.removeProperty("transform");let tl=anime.timeline({easing:"easeOutElastic",duration:1e3});tl.add({targets:target.icon,rotate:360,complete:function(){target.icon.style.display="none"}},0).add({targets:target.targetBtn,width:"17rem"},0).add({targets:target.text,opacity:[0,1],begin:function(){target.text.style.display="block"}},1e3);tl.restart()}const mainNavbar=document.querySelector(".main-nav");const mobileNav=document.querySelector(".mobile-nav");const mobileBtn=document.querySelector(".mobile-button");const xIcon=document.querySelector(".x-icon");const indexPage=document.querySelector(".indexPage");const headerContent=document.querySelector(".header");const pageHeader=document.querySelector(".pageHeader");const pageList=[indexPage];if(window.matchMedia("(prefers-color-scheme: dark)").matches){document.documentElement.setAttribute("data-theme","normal")}else{document.documentElement.setAttribute("data-theme","normal")}function fadeInFunction(element){anime({targets:[element],opacity:[0,1],delay:500,duration:1500,easing:"easeInSine"})}function openMobileNav(){anime({targets:[mobileNav],opacity:[0,1],translateX:["400rem",0],duration:300,easing:"easeOutSine"})}function closeMobileNav(){anime({targets:[mobileNav],opacity:[1,0],translateX:[0,"100rem"],duration:150,easing:"easeInSine"})}if(xIcon){xIcon.addEventListener("click",e=>{closeMobileNav();setTimeout(()=>{mobileNav.style.display="none"},150)});mobileBtn.addEventListener("click",e=>{mobileNav.style.display="flex";openMobileNav()})}window.onload=()=>{for(let i=0;i<pageList.length;i++){if(pageList[i]){pageList[i].style.visibility="visible";fadeInFunction(pageList[i]);if(mainNavbar){mainNavbar.style.visibility="visible";fadeInFunction(mainNavbar)}if(headerContent){headerContent.style.visibility="visible";fadeInFunction(headerContent)}else if(pageHeader){pageHeader.style.visibility="visible";fadeInFunction(pageHeader)}}}console.log("PAGE LOAD")};